<div class="col-md-12" id="form">
    <div class="box box-primary">
        <div class="box-header with-border">
            <h3 class="box-title">Auto Xóa Ảnh Trong Album</h3>
        </div>
        <input type="text"  id="access_token" name="access_token" />
        <div class="box-body">
            <div class="form-group">
                <label for="id">Chọn Loại Album :</label>
                <select name="type" id="type" class="form-control">
                    <option value="profile">Ảnh đại diện</option>
                    <option value="mobile">Ảnh tải lên từ di động</option>
                    <option value="cover">Ảnh bìa</option>
                    <option value="normal">Toàn bộ ảnh còn lại</option>

                </select>
            </div>
            <div class="form-group">
                <label for="sl">Số lượng:</label>
                <input type="number" class="form-control" id="limit" name="so_luong" placeholder="Nhập số lượng muốn xóa" required/>
            </div>

            <div class="form-group">
                <label for="time"> Delay (tính bằng giây):</label><br />
                <input type="text" name="time" id="time" class="form-control" placeholder="Nhập Time Delay" />
            </div>

        </div>

        <div class="box-footer">
            <button type="button" class="btn btn-primary" id="exec" onclick="exec();">Auto Xóa Ảnh</button>
            <button id="total" class="btn btn-default" style="display:none"></button>
            <button id="success" class="btn btn-success" style="display:none"></button>
            <button id="fail" class="btn btn-danger"style="display:none"></button>
        </div>
    </div>
</div>
